      DATA  PERTST / 1D0,1D0,2D0,1D0,.3158D0,.07407D0,.01391D0,
     *               .002182D0,.0002945D0,.00003492D0,.000003692D0,
     *               .0000003524D0,1D0,1D0,.5D0,.1667D0,.04167D0,
